SELECT nnd mc,COUNT(*) zcount FROM( SELECT case WHEN TIMESTAMPDIFF(YEAR,csrq,CURDATE())<=15 THEN '15岁以下' WHEN TIMESTAMPDIFF(YEAR,csrq,CURDATE())>=16 AND TIMESTAMPDIFF(YEAR,csrq,CURDATE())<=20 THEN '16-20岁' WHEN TIMESTAMPDIFF(YEAR,csrq,CURDATE())>=21 AND TIMESTAMPDIFF(YEAR,csrq,CURDATE())<=25 THEN '21-25岁' WHEN TIMESTAMPDIFF(YEAR,csrq,CURDATE())>=26 AND TIMESTAMPDIFF(YEAR,csrq,CURDATE())<=30 THEN '26-30岁' WHEN TIMESTAMPDIFF(YEAR,csrq,CURDATE())>=31 THEN '31岁以上' end as nnd FROM gxxs01_bzks WHERE csrq!='' AND rxnj='2017'
AND zymc=#{zymc}
AND bh=#{bh}
)gxxs01_bzks GROUP BY nnd

