SELECT avg(dwmyd) zcount,year mc FROM gxxx_bysqk WHERE 1=1

  AND zymc=#{zymc}
 GROUP BY year ORDER BY year

    SELECT  nnd mc,COUNT(*) zcount FROM (
        SELECT
 			 case
	   WHEN qxx<=1500 THEN '1500及以下'
				WHEN qxx>=1501 AND qxx<=2000 THEN '1501-2000'
				WHEN qxx>=2001 AND qxx<=2500 THEN '2001-2500'
				WHEN qxx>=2501 AND qxx<=3000 THEN '2501-3000'
 				WHEN qxx>=3001 THEN '3000以上' 
				end as nnd FROM  gxxx_bysqk WHERE 1=1 

				     AND year=#{nf}


				     AND zymc=#{zymc}


				     AND pycc=#{lb}

     ) gxxx_bysqk   GROUP BY nnd

