SELECT zgh zgh,xm xm,xbid xbm,dept_id yxsh,dept_name yxmc,zzmmmc zzmmm FROM tr_jzg_basic WHERE zyjszw_dj in ('中级','副高级')

 AND dept_name=#{zymc}


 AND year=#{nf}

  SELECT (SELECT COUNT(1) FROM tr_jzg_basic WHERE xlmc='本科' AND year=a.year

   AND dept_name=#{zymc}
  ) 'dm',
  (SELECT COUNT(1) FROM tr_jzg_basic WHERE xlmc='大专' AND year=a.year

   AND dept_name=#{zymc}
  ) 'bl1',
(SELECT COUNT(1) FROM tr_jzg_basic WHERE xlmc='研究生' AND year=a.year

   AND dept_name=#{zymc}
  ) 'bl2' ,
(SELECT COUNT(1) FROM tr_jzg_basic WHERE xlmc='中专' AND year=a.year

   AND dept_name=#{zymc}
  ) 'bl3' ,year mc
FROM tr_jzg_basic a GROUP BY year

SELECT zgh zgh,xm xm,xbid xbm,dept_id yxsh,is_jz isjz,dept_name,xlmc,is_jslx isjslx,zzmmmc zzmmm,zyjszw_dj zyjszwdj,is_ssjzg isssjzg FROM tr_jzg_basic WHERE 1=1

AND dept_name=#{zymc}


AND year=#{nf}

  SELECT (SELECT COUNT(1) FROM tr_jzg_basic WHERE zyjszw_dj='初级' AND year=a.year

   AND dept_name=#{zymc}
  ) 'dm',
  (SELECT COUNT(1) FROM tr_jzg_basic WHERE zyjszw_dj='中级' AND year=a.year

   AND dept_name=#{zymc}
  ) 'bl1',
(SELECT COUNT(1) FROM tr_jzg_basic WHERE zyjszw_dj='副高级' AND year=a.year

   AND dept_name=#{zymc}
  ) 'bl2' ,year mc
FROM tr_jzg_basic a GROUP BY year

SELECT COUNT(1)/(SELECT COUNT(1) FROM tr_jzg_basic WHERE dept_name=a.dept_name) zj,dept_name mc FROM tr_jzg_basic a WHERE is_ssjzg='1'

 AND year=#{nf}
 GROUP BY dept_name ORDER BY zj

SELECT (SELECT COUNT(1) FROM tr_jzg_basic WHERE is_jz='1' AND year=a.year

 AND dept_name=#{zymc}
) 'bl2',
(SELECT COUNT(1) FROM tr_jzg_basic WHERE is_jz='0' AND year=a.year

 AND dept_name=#{zymc}
) 'bl1',year mc
FROM tr_jzg_basic a GROUP BY year ORDER BY year

