SELECT xm dm,zymc mc,bjmc bl1,kcmc bl2,xn bl3,kccj zj FROM gxjx01_bzks_kscj
where 1=1 
AND zymc=#{zymc}
  AND bjmc=#{bjmc}
  AND kcmc=#{kcmc}
   AND xn=#{nf}
  GROUP BY kcmc

SELECT KCMC mc FROM gxjx01_bzks_kscj WHERE
KCMC IS NOT NULL
 AND zymc=#{zymc}
AND bjmc=#{bjmc}
 GROUP BY KCMC

  SELECT nnd mc,COUNT(1) zcount  FROM(  
	 SELECT  
	 CASE 

	  WHEN KCCJ<60 THEN '0-59'
		WHEN KCCJ>=60 AND KCCJ<=70 THEN '60-70'   
	    WHEN KCCJ>70 AND KCCJ<80 THEN '71-79'
		WHEN KCCJ>=80 THEN '80-100'  
	 END 
	 AS nnd FROM gxjx01_bzks_kscj 
	 where 1=1
	 AND zymc=#{zymc}
     AND bjmc=#{bjmc}
     AND kcmc=#{kcmc}
      AND xn=#{nf}
     ) gxjx01_bzks_kscj 
	   GROUP BY nnd

SELECT xm dm,zymc mc,bjmc bl1,kcmc bl2,xn bl3,kccj zj FROM gxjx01_bzks_kscj
where 1=1 
AND zymc=#{zymc}
  AND bjmc=#{bjmc}
  AND kcmc=#{kcmc}
   AND xn=#{nf}

   AND kccj BETWEEN 0 AND 59 
   AND kccj BETWEEN 60 AND 70 
   AND kccj BETWEEN 71 AND 79 
   AND kccj  >= 80 

  GROUP BY kcmc

select(SELECT COUNT(1) FROM gxjx01_bzks_xssgyy where sgyy='迟到' AND xn=a.xn 
AND kcm=#{kcmc}
) bl1,
(SELECT COUNT(1) FROM gxjx01_bzks_xssgyy where sgyy='早退' AND xn=a.xn 
AND kcm=#{kcmc}) bl2,
(SELECT COUNT(1) FROM gxjx01_bzks_xssgyy where sgyy='请假' AND xn=a.xn 
AND kcm=#{kcmc}) bl3,xn mc FROM gxjx01_bzks_xssgyy a where 1=1 
AND kcm=#{kcmc}
GROUP BY xn

